mapper.select:
select * from sys_company_user where cid = #{cid}
                               and DATE_FORMAT(invited_at,'%Y-%m-%d') <= DATE_FORMAT(#{endDate},'%Y-%m-%d')
                               and DATE_FORMAT(invited_at,'%Y-%m-%d') >= DATE_FORMAT(#{startDate},'%Y-%m-%d')

mapper.insert:
insert into sys_company_user (cid, uid, invited_at, invited_by, invited_by_type, created_at, created_by, updated_at, updated_by)
values (#{cid,jdbcType=INTEGER}, #{uid,jdbcType=INTEGER}
       , #{invited_at,jdbcType=TIMESTAMP}, #{invited_by,jdbcType=INTEGER}, #{invited_by_type,jdbcType=INTEGER}
       , #{created_at,jdbcType=TIMESTAMP}, #{created_by,jdbcType=INTEGER}
       , #{updated_at,jdbcType=TIMESTAMP}, #{updated_by,jdbcType=INTEGER})
